mapper.insert:
insert into j_member_recommend (id, member_id, plate, 
  recommend_order, status, add_time, 
  update_time, type, operator_id
  )
values (#{id,jdbcType=BIGINT}, #{memberId,jdbcType=BIGINT}, #{plate,jdbcType=TINYINT}, 
  #{recommendOrder,jdbcType=INTEGER}, #{status,jdbcType=TINYINT}, #{addTime,jdbcType=TIMESTAMP}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{type,jdbcType=TINYINT}, #{operatorId,jdbcType=INTEGER}
  )

mapper.select:
SELECT mr.id, mr.member_id, mr.plate, mr.recommend_order, mr.status, mr.add_time, mr.update_time, mr.type, mr.operator_id, m.name as memberName, c.company_name as companyName, u.user_name as userName FROM j_member_recommend mr INNER JOIN j_member m on mr.member_id = m.id LEFT JOIN j_company c on c.id = m.company_id LEFT JOIN sys_user u on mr.operator_id = u.sys_user_id WHERE m.check_status = 4 and m.status = 1
AND m.name like CONCAT("%",#{memberName,jdbcType=TIMESTAMP},"%")
AND mr.member_id = #{memberId,jdbcType=BIGINT}
AND mr.plate = #{plate,jdbcType=TINYINT}
AND mr.recommend_order = #{recommendOrder,jdbcType=INTEGER}
AND mr.status = #{status,jdbcType=TINYINT}
AND mr.type = #{type,jdbcType=TINYINT}
