mapper.insert:
insert into j_product_recommend (id, product_id, plate, 
  recommend_order, status, add_time, 
  update_time, type, operator_id
  )
values (#{id,jdbcType=BIGINT}, #{productId,jdbcType=BIGINT}, #{plate,jdbcType=TINYINT}, 
  #{recommendOrder,jdbcType=INTEGER}, #{status,jdbcType=TINYINT}, #{addTime,jdbcType=TIMESTAMP}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{type,jdbcType=TINYINT}, #{operatorId,jdbcType=TINYINT}
  )

mapper.select:
SELECT pr.id, pr.product_id, pr.plate, pr.recommend_order, pr.status, pr.add_time, pr.update_time, pr.type, pr.operator_id, p.title as productName, c.name as categoryName, u.user_name as userName FROM j_product_recommend pr INNER JOIN j_product p on pr.product_id = p.id LEFT JOIN j_class c on c.id = p.class_id LEFT JOIN sys_user u on pr.operator_id = u.sys_user_id WHERE p.status = 4
AND p.title like CONCAT("%",#{productName,jdbcType=TIMESTAMP},"%")
AND pr.product_id = #{productId,jdbcType=BIGINT}
AND pr.plate = #{plate,jdbcType=TINYINT}
AND pr.recommend_order = #{recommendOrder,jdbcType=INTEGER}
AND pr.status = #{status,jdbcType=TINYINT}
AND pr.type = #{type,jdbcType=TINYINT}
